import React, {Component} from 'react';
import '././bootstrap/bootstrap.css';
class NameForm extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {value : null,count:0}
    this.setState((props)=>{
      return {value: props,
        count: this.count + 1  
      }
    })

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    
  }
    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('Welcome: ' + this.state.value);
      event.preventDefault();
    }

    IncrementItem = () => {
      this.setState({ count: this.state.count + 1 });
    }

  render() {
    return (
      
      <div className="container-fluid bg-light" onLoad={this.IncrementItem}>
        <div className="row">
          <div className="col"><form className="form-group" onSubmit={this.handleSubmit}>
        <label className="text-justify Lemonada">
    <h4>Enter a NickName or your Name:{this.state.count}</h4>
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="" />
      </form></div>
          <div className="col"><div>
        <p ><h4>Welcome</h4><h5> {this.state.value?this.state.value:'( ͡° ͜ʖ ͡°)'}</h5></p>
        <br></br>
      </div></div>

        </div>

      
      

    </div>  
    );

  }
}

  export default NameForm;
